const { isHex, u8aToHex } = require("@polkadot/util");
const { encodeAddress, decodeAddress } = require("@polkadot/util-crypto");
let defaultType = 42;

function addressIdToAddress(value) {
  value = `0x${value}`;
  return isHex(value) ? encodeAddress(value, defaultType) : value;
}

function addressToAddressId(value) {
  return u8aToHex(decodeAddress(value, false, defaultType));
}

function setNetworkDefault(type) {
  defaultType = type;
}
module.exports = { addressIdToAddress, addressToAddressId, setNetworkDefault };
